<?php 	global $accommodation_title, $accommodation_review_fields; ?>	<!--full-width content-->	<section class="full-width review-section" style="display:none">		<article class="static-content">			<form id="review-form" method="post" action="" class="review">				<h1><?php echo sprintf(__('We would like to know your opinion about %s', 'bookyourtravel'), $accommodation_title); ?></h1>				<div class="error" style="display:none;"><span><p></p></span></div>				<p><?php _e('Please score the following elements of your stay:', 'bookyourtravel'); ?></p>				<table>					<tr>						<th><?php _e('Element', 'bookyourtravel'); ?></th>						<?php for ( $i = 1; $i <= 10; $i++ ) {							echo '<th>' . $i . '</th>';						} ?>					</tr>					<?php					foreach($accommodation_review_fields as $field_key => $field_label) {					?>					<tr>						<th><?php echo $field_label; ?></th>						<?php for ( $i = 1; $i <= 10; $i++ ) {							echo '<td><input type="radio" id="reviewField_' . $field_key . '_' . $i . '" value="' . $i . '" name="reviewField_' . $field_key . '" /></td>';						} ?>					</tr>					<?php } ?>				</table>				<h3><?php _e('What did you like about the accommodation?', 'bookyourtravel'); ?></h3>				<div class="f-item">					<textarea id="likes" name='likes' rows="10" cols="10" ></textarea>				</div>								<h3><?php _e('What did you not like about the accommodation?', 'bookyourtravel'); ?></h3>				<div class="f-item">					<textarea id="dislikes" name='dislikes' rows="10" cols="10" ></textarea>				</div>							<a href="javascript:void(0);" class="gradient-button cancel_review" title="<?php _e('Cancel', 'bookyourtravel') ?>" id="cancel-review"><?php _e('Cancel', 'bookyourtravel') ?></a>				<input type="submit" value="<?php _e('Submit review', 'bookyourtravel'); ?>" id="submit_review" name="submit_review" class="gradient-button" />			</form>		</article>	</section>	<!--//full-width content--><?php ?>