<?php/** * Remove password email text if option for users to set their own password is enabled in Theme settings. */function remove_password_email_text ( $text ) {	$let_users_set_pass = of_get_option('let_users_set_pass', 0);	if ($text == 'A password will be e-mailed to you.' && $let_users_set_pass)		$text = '';	return $text;}add_filter( 'gettext', 'remove_password_email_text' );?>