_fields['accommodation_cancellation_prepayment']) ? $accommodation_custom_fields['accommodation_cancellation_prepayment'][0] : '';	$accommodation_children_and_extra_beds = isset($accommodation_custom_fields['accommodation_children_and_extra_beds']) ? $accommodation_custom_fields['accommodation_children_and_extra_beds'][0] : '';	$accommodation_pets = isset($accommodation_custom_fields['accommodation_pets']) ? $accommodation_custom_fields['accommodation_pets'][0] : '';	$accommodation_accepted_credit_cards = isset($accommodation_custom_fields['accommodation_accepted_credit_cards']) ? $accommodation_custom_fields['accommodation_accepted_credit_cards'][0] : '';	$accommodation_activities = isset($accommodation_custom_fields['accommodation_activities']) ? $accommodation_custom_fields['accommodation_activities'][0] : '';	$accommodation_internet = isset($accommodation_custom_fields['accommodation_internet']) ? $accommodation_custom_fields['accommodation_internet'][0] : '';	$accommodation_parking = isset($accommodation_custom_fields['accommodation_parking']) ? $accommodation_custom_fields['accommodation_parking'][0] : '';	$accommodation_latitude = isset($accommodation_custom_fields['accommodation_latitude']) ? $accommodation_custom_fields['accommodation_latitude'][0] : '';	$accommodation_longitude = isset($accommodation_custom_fields['accommodation_longitude']) ? $accommodation_custom_fields['accommodation_longitude'][0] : '';	$accommodation_images = isset($accommodation_custom_fields['accommodation_images']) ? $accommodation_custom_fields['accommodation_images'][0] : '';	// get current time to be used with fetching vacancies if no querystring parameters provided	$now = time();	$date = date('Y-m-d', $now);		$accommodation_date_from = $date;	$accommodation_date_to = date('Y-m-d', strtotime("+1 day", $now));		// retrieve from and to dates from querystring if provided	if (isset($_GET['from']) || isset($_GET['to'])) {		parse_str(urldecode($_SERVER['QUERY_STRING']), $get_array);		if (isset($get_array['from']))			$accommodation_date_from = date('Y-m-d', strtotime($get_array['from']));		if (isset($get_array['to']))			$accommodation_date_to = date('Y-m-d', strtotime($get_array['to']));	}	$accommodation_location = null;	if (isset($accommodation_custom_fields['accommodation_location_post_id'])) {		$location_id = $accommodation_custom_fields['accommodation_location_post_id'][0];		$accommodation_location = get_post($location_id);		if ($accommodation_location)			$accommodation_location_title = $accommodation_location->post_title;	}		$accommodation_review_fields = list_accommodation_review_fields();	$accommodation_room_types = isset($accommodation_custom_fields['room_types']) ? $accommodation_custom_fields['room_types'][0] : '';	$accommodation_room_types = !empty($accommodation_room_types) ? unserialize($accommodation_room_types) : array();	$accommodation_facilities = wp_get_post_terms($accommodation_id, 'facility', array("fields" => "all"));	// include various forms (booking, review, confirmation, dates)	if (is_user_logged_in()) 		get_template_part('includes/parts/accommodation', 'review-form'); 	get_template_part('includes/parts/accommodation', 'contact-form');		?>			<!--accommodation three-fourth content-->	<section class="three-fourth">	<?php	get_template_part('includes/parts/accommodation', 'booking-form');	get_template_part('includes/parts/accommodation', 'confirmation-form');	get_template_part('includes/parts/accommodation', 'dates-form');	?>		<script>		window.reviewFields = new Array();		<?php if (count($accommodation_review_fields) > 0) { 		foreach ($accommodation_review_fields as $field_key=>$field_label) {		?>		reviewFields.push("<?php echo $field_key; ?>");		<?php }		} ?>			window.accommodationId = <?php echo $accommodation_id; ?>;		window.formSingleError = '<?php _e('You failed to provide 1 field. It has been highlighted below.', 'bookyourtravel'); ?>';		window.formMultipleError = '<?php _e('You failed to provide {0} fields. They have been highlighted below.', 'bookyourtravel');  ?>';		window.accommodationDateFrom = '<?php echo $accommodation_date_from; ?>';		window.accommodationDateTo = '<?php echo $accommodation_date_to; ?>';		window.accommodationLocationLatitude = '<?php echo $accommodation_latitude; ?>';		window.accommodationLocationLongitude = '<?php echo $accommodation_longitude; ?>';		window.accommodationTitle = '<?php echo $accommodation_title; ?>';		window.accommodationAddress = '<?php echo $accommodation_address; ?>';		window.accommodationWebsite = '<?php echo $accommodation_web_site; ?>';		window.reviewFormLikesError = '<?php _e('Please enter your likes', 'bookyourtravel'); ?>';		window.reviewFormDislikesError = '<?php _e('Please enter your dislikes', 'bookyourtravel'); ?>';		window.bookingFormFirstNameError = '<?php _e('Please enter your first name', 'bookyourtravel'); ?>';		window.bookingFormLastNameError = '<?php _e('Please enter your last name', 'bookyourtravel'); ?>';		window.bookingFormEmailError = '<?php _e('Please enter valid email address', 'bookyourtravel'); ?>';		window.bookingFormPhoneError = '<?php _e('Please enter your phone number', 'bookyourtravel'); ?>';		window.bookingFormConfirmEmailError1 = '<?php _e('Please provide a confirm email', 'bookyourtravel'); ?>';		window.bookingFormConfirmEmailError2 = '<?php _e('Please enter the same email as above', 'bookyourtravel'); ?>';		window.bookingFormAddressError = '<?php _e('Please enter your address', 'bookyourtravel'); ?>';		window.bookingFormCityError = '<?php _e('Please enter your city', 'bookyourtravel'); ?>';				window.bookingFormZipError = '<?php _e('Please enter your zip code', 'bookyourtravel'); ?>';				window.bookingFormCountryError = '<?php _e('Please enter your country', 'bookyourtravel'); ?>';			window.inquiryFormMessageError = '<?php _e('Please enter your message', 'bookyourtravel'); ?>';		window.inquiryFormNameError = '<?php _e('Please enter your full name', 'bookyourtravel'); ?>';		window.inquiryFormEmailError = '<?php _e('Please enter your email address', 'bookyourtravel'); ?>';		window.inquiryFormPhoneError = '<?php _e('Please enter your phone number', 'bookyourtravel'); ?>';	</script>	<?php if (count($accommodation_images) > 0) { ?>	<!--gallery-->	<section class="gallery" id="crossfade">	<?php 	$image_array = unserialize($accommodation_images);	for ( $i = 0; $i < count($image_array); $i++ ) { 		$image_meta_id = $image_array[$i]['image'];		$image_src = wp_get_attachment_image_src($image_meta_id, 'full');			$image_src = $image_src[0];		if (!empty($image_src)) { 			echo '<img src="' . $image_src . '" alt="" width="850" height="531" />';		}	}?>	</section>	<!--//gallery-->	<?php } ?>	<!--inner navigation-->	<nav class="inner-nav">		<ul>			<li class="availability"><a href="#availability" title="<?php _e('Availability', 'bookyourtravel'); ?>"><?php _e('Availability', 'bookyourtravel'); ?></a></li>			<li class="description"><a href="#description" title="<?php _e('Description', 'bookyourtravel'); ?>"><?php _e('Description', 'bookyourtravel'); ?></a></li>			<li class="facilities"><a href="#facilities" title="<?php _e('Facilities', 'bookyourtravel'); ?>"><?php _e('Facilities', 'bookyourtravel'); ?></a></li>			<?php if ((!empty($accommodation_latitude)) && (!empty($accommodation_longitude))) { ?>			<li class="location"><a href="#location" title="<?php _e('Location', 'bookyourtravel'); ?>"><?php _e('Location', 'bookyourtravel'); ?></a></li>			<?php } // endif ((!empty($accommodation_latitude)) && (!empty($accommodation_longitude))) ?>							<li class="reviews"><a href="#reviews" title="<?php _e('Reviews', 'bookyourtravel'); ?>"><?php _e('Reviews', 'bookyourtravel'); ?></a></li>			<?php if (isset($accommodation_location)) { ?>			<li class="things-to-do"><a href="#things-to-do" title="<?php _e('Things to do', 'bookyourtravel'); ?>"><?php _e('Things to do', 'bookyourtravel'); ?></a></li>			<?php } //endif (!empty($accommodation_location)) ?>		</ul>	</nav>	<!--//inner navigation-->	<section id="availability" class="tab-content">		<article>			<h1><?php _e('Availability', 'bookyourtravel'); ?></h1>			<?php 			if (!$accommodation_is_self_catered) {								$vacancy_room_types = list_vacancy_room_types($accommodation_id, $accommodation_date_from, $accommodation_date_to);				if (count($vacancy_room_types) > 0) { ?>					<div class="text-wrap">					<a onclick="toggleLightbox('dates_lightbox');" class="gradient-button right set-date" title="<?php _e('Change dates', 'bookyourtravel'); ?>"><?php _e('Change dates', 'bookyourtravel'); ?></a>					<p><?php echo sprintf(__('Available rooms from <span class="date">%s</span> to <span class="date">%s</span>', 'bookyourtravel'), $accommodation_date_from, $accommodation_date_to); ?></p>				</div>				<h1><?php _e('Rooms', 'bookyourtravel'); ?></h1>				<ul class="room-types">					<?php 					// Loop through the items returned					foreach ( $vacancy_room_types as $vacancy_room_type ) {						$room_type_post_id = $vacancy_room_type->room_type_id;						$price_decimal_places = (int)of_get_option('price_decimal_places', 0);						$vacancy_price = number_format($vacancy_room_type->min_price, $price_decimal_places);						$room_type_post = get_post($room_type_post_id);						$vacancy_number_of_rooms = $vacancy_room_type->room_count;												$total_price = get_total_price($accommodation_id, $accommodation_date_from, $accommodation_date_to, $room_type_post_id, 1);					?>					<!--room_type_post-->					<li id="room_type_<?php echo $room_type_post_id; ?>">						<?php 						$room_type_custom_fields = get_post_custom( $room_type_post_id);						$room_type_title = $room_type_post->post_title;						$room_type_description = $room_type_post->post_content;						$room_type_description = apply_filters('the_content', $room_type_description);						$room_type_max_count = isset($room_type_custom_fields['room_type_max_count']) ? $room_type_custom_fields['room_type_max_count'][0] : '';						$room_type_meta = isset($room_type_custom_fields['room_type_meta']) ? $room_type_custom_fields['room_type_meta'][0] : '';						$room_type_bed_size = isset($room_type_custom_fields['room_type_bed_size']) ? $room_type_custom_fields['room_type_bed_size'][0] : ''; 						$room_type_room_size = isset($room_type_custom_fields['room_type_room_size']) ? $room_type_custom_fields['room_type_room_size'][0] : '';											$room_type_image = '';						if ( has_post_thumbnail($room_type_post_id)) {							$room_type_image = wp_get_attachment_image_src( get_post_thumbnail_id($room_type_post_id), 'medium');							$room_type_image = $room_type_image[0];							$room_type_image_full = wp_get_attachment_image_src( get_post_thumbnail_id($room_type_post_id), 'full');							$room_type_image_full = $room_type_image_full[0];						}												if (!empty($room_type_image)) {?>						<figure class="left">							<img src="<?php echo $room_type_image; ?>" alt="" width="270" height="152" />							<a href="<?php echo $room_type_image_full; ?>" class="image-overlay" rel="prettyPhoto[gallery1]"></a>						</figure>						<?php } ?>						<div class="meta room_type">							<h2><?php echo $room_type_title; ?></h2>							<?php if (!empty($room_type_meta)) { ?>							<p><?php echo $room_type_meta; ?></p>							<?php } ?>							<a href="javascript:void(0)" title="more info" class="more-info"><?php _e('+ more info', 'bookyourtravel'); ?></a>						</div>						<div class="room-information">							<div class="row">								<span class="first"><?php _e('Max:', 'bookyourtravel'); ?></span>								<span class="second">									<?php for ( $j = 0; $j < $room_type_max_count; $j++ ) {?>									<img src="<?php echo get_template_directory_uri(); ?>/images/ico/person.png" alt="" />									<?php } ?>								</span>							</div>							<div class="row">								<span class="first"><?php _e('Price