ocation_nightlife ?>			<?php } // endif (!empty($location_nightlife)) ?>			<?php if (!empty($location_culture_and_history)) { ?>			<h1><?php _e('Culture and history', 'bookyourtravel'); ?></h1>				<?php if (!empty($location_culture_and_history_image)) { ?>			<img src="<?php echo $location_culture_and_history_image; ?>" alt="<?php _e('Culture &amp; history', 'bookyourtravel'); ?>" />			<?php } ?>			<?php echo $location_culture_and_history ?>				<?php } // endif (!empty($location_culture_and_history)) ?>			<hr />			<a href="<?php echo get_permalink($accommodation_location->ID); ?>" class="gradient-button right" title="<?php _e('Read more', 'bookyourtravel'); ?>"><?php _e('Read more', 'bookyourtravel'); ?></a>		</article>	</section>	<!--//things-to-do-->	<?php } ?>	</section>	<!--//accommodation content-->	<?php} // end ifget_sidebar('right-accommodation'); get_footer(); ?>